import React, { useEffect, useState } from 'react';

const VentasTable = () => {
  const [ventas, setVentas] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8000/api/ventas/')
      .then(res => res.json())
      .then(data => setVentas(data))
      .catch(err => console.error('Error al obtener ventas:', err));
  }, []);

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">Listado de Ventas</h2>
      <table className="min-w-full bg-white border border-gray-200">
        <thead>
          <tr>
            <th className="py-2 px-4 border-b">ID</th>
            <th className="py-2 px-4 border-b">Cantidad</th>
            <th className="py-2 px-4 border-b">Total</th>
            <th className="py-2 px-4 border-b">Cliente</th>
            <th className="py-2 px-4 border-b">Producto</th>
            <th className="py-2 px-4 border-b">Fecha</th>
          </tr>
        </thead>
        <tbody>
          {ventas.map((venta, index) => (
            <tr key={index} className="hover:bg-gray-100">
              <td className="py-1 px-4 text-center">{venta.id}</td>
              <td className="py-1 px-4 text-center">{venta.cantidad}</td>
              <td className="py-1 px-4 text-center">â‚¬ {venta.total}</td>
              <td className="py-1 px-4 text-center">{venta.cliente}</td>
              <td className="py-1 px-4 text-center">{venta.producto}</td>
              <td className="py-1 px-4 text-center">{venta.tiempo}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default VentasTable;
