import React, { useEffect, useState } from 'react';

const VentasTable = () => {
  const [ventas, setVentas] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8000/api/ventas/')
      .then(res => res.json())
      .then(data => setVentas(data))
      .catch(err => console.error('Error al obtener ventas:', err));
  }, []);

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold text-center mb-6">Listado de Ventas</h2>
      <div className="overflow-x-auto">
        <table className="min-w-full bg-white border border-gray-200 shadow-md rounded-lg">
          <thead className="bg-gray-100">
            <tr>
              <th className="py-2 px-4 border-b">ID</th>
              <th className="py-2 px-4 border-b">Cantidad</th>
              <th className="py-2 px-4 border-b">Total</th>
              <th className="py-2 px-4 border-b">Cliente</th>
              <th className="py-2 px-4 border-b">Producto</th>
              <th className="py-2 px-4 border-b">Fecha</th>
            </tr>
          </thead>
          <tbody>
            {ventas.map((venta) => (
              <tr key={venta.id} className="hover:bg-gray-50">
                <td className="py-2 px-4 text-center">{venta.id}</td>
                <td className="py-2 px-4 text-center">{venta.cantidad}</td>
                <td className="py-2 px-4 text-center">
                  â‚¬ {parseFloat(venta.total).toLocaleString("es-ES", { minimumFractionDigits: 2 })}
                </td>
                <td className="py-2 px-4 text-center">{venta.cliente.nombre}</td>
                <td className="py-2 px-4 text-center">{venta.producto.nombre}</td>
                <td className="py-2 px-4 text-center">{venta.tiempo.fecha}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default VentasTable;
