import React, { useEffect, useState } from 'react';

const VentasTable = () => {
  const [ventas, setVentas] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8000/api/ventas/')
      .then(res => res.json())
      .then(data => setVentas(data))
      .catch(err => console.error('Error al obtener ventas:', err));
  }, []);

  return (
    <div className="p-6 bg-[#f5f5f5] min-h-screen">
      <h2 className="text-3xl font-bold text-center text-[#db0011] mb-6">Listado de Ventas</h2>
      <div className="overflow-x-auto shadow-lg rounded-2xl border border-gray-300">
        <table className="min-w-full bg-white text-gray-800 text-sm">
          <thead className="bg-[#db0011] text-white text-left">
            <tr>
              <th className="py-3 px-4">ID</th>
              <th className="py-3 px-4">Cantidad</th>
              <th className="py-3 px-4">Total</th>
              <th className="py-3 px-4">Cliente</th>
              <th className="py-3 px-4">Producto</th>
              <th className="py-3 px-4">Fecha</th>
            </tr>
          </thead>
          <tbody>
            {ventas.map((venta) => (
              <tr key={venta.id} className="hover:bg-gray-100 border-t border-gray-200">
                <td className="py-2 px-4">{venta.id}</td>
                <td className="py-2 px-4">{venta.cantidad}</td>
                <td className="py-2 px-4">â‚¬ {venta.total}</td>
                <td className="py-2 px-4">{venta.cliente?.nombre}</td>
                <td className="py-2 px-4">{venta.producto?.nombre}</td>
                <td className="py-2 px-4">{venta.tiempo?.fecha}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default VentasTable;
